import React from 'react';

const Testimonials = () => {
  const testimonials = [
    {
      text: "Le Dr TOURE a su me rassurer et m'accompagner tout au long de mon traitement. Son professionnalisme et sa bienveillance ont fait toute la différence.",
      author: "Fatoumata DIALLO"
    },
    {
      text: "Une intervention chirurgicale réussie grâce à l'expertise du Dr TOURE. Je recommande vivement ses services pour leur qualité et leur efficacité.",
      author: "Amadou TRAORE"
    },
    {
      text: "Le Dr TOURE explique tout avec clarté et patience. On se sent écouté et en confiance. Une téléconsultation très rassurante.",
      author: "Mariam KEITA"
    },
    {
      text: "Grâce au Dr TOURE, j'ai retrouvé une vie normale après mon opération. Son savoir-faire et son humanité sont remarquables.",
      author: "Boubacar SANGARE"
    },
    {
      text: "Un médecin exceptionnel qui allie compétence technique et écoute patiente. Je suis reconnaissante pour les soins reçus.",
      author: "Aïcha COULIBALY"
    },
    {
      text: "Le Dr TOURE a su diagnostiquer et traiter efficacement ma pathologie. Son expertise et son dévouement sont exemplaires.",
      author: "Ibrahim DIARRA"
    }
  ];

  return (
    <section id="testimonials" className="testimonials">
      <div className="container">
        <h2 className="section-title">Témoignages de patients</h2>

        <div className="testimonials-grid">
          {testimonials.map((testimonial, index) => (
            <div key={index} className="testimonial-card">
              <p className="testimonial-text">{testimonial.text}</p>
              <p className="testimonial-author">— {testimonial.author}</p>
            </div>
          ))}
        </div>
      </div>
    </section>
  );
};

export default Testimonials;
