import React from 'react';
import { FaStethoscope, FaUserMd, FaVideo, FaHeartbeat } from 'react-icons/fa';

const Services = () => {
  const services = [
    {
      icon: <FaStethoscope />,
      title: "Chirurgie Générale",
      description: "Interventions chirurgicales précises et sécurisées pour traiter diverses pathologies générales avec expertise et professionnalisme."
    },
    {
      icon: <FaUserMd />,
      title: "Consultations Médicales",
      description: "Diagnostic complet et suivi personnalisé pour toutes les pathologies, du diagnostic initial au suivi post-opératoire."
    },
    {
      icon: <FaVideo />,
      title: "Téléconsultations",
      description: "Consultations à distance pour une prise en charge rapide et efficace, même si vous êtes éloigné géographiquement."
    },
    {
      icon: <FaHeartbeat />,
      title: "Chirurgie Spécialisée",
      description: "Expertise dans diverses spécialités chirurgicales avec des techniques modernes et des résultats optimaux."
    }
  ];

  const pathologies = [
    "Chirurgie digestive",
    "Chirurgie urologique", 
    "Chirurgie gynécologique",
    "Chirurgie traumatologique",
    "Chirurgie oncologique",
    "Chirurgie pédiatrique",
    "Chirurgie vasculaire",
    "Chirurgie plastique et reconstructrice"
  ];

  return (
    <section id="services" className="services">
      <div className="container">
        <h2 className="section-title">Mes Services</h2>

        <div className="services-grid">
          {services.map((service, index) => (
            <div key={index} className="service-card">
              <div className="service-icon">{service.icon}</div>
              <h3>{service.title}</h3>
              <p>{service.description}</p>
            </div>
          ))}
        </div>

        <div style={{ marginTop: '4rem' }}>
          <h3 style={{ textAlign: 'center', fontSize: '2rem', marginBottom: '2rem', color: '#1e293b' }}>
            Pathologies Prises en Charge
          </h3>
          <div style={{ 
            display: 'grid', 
            gridTemplateColumns: 'repeat(auto-fit, minmax(250px, 1fr))', 
            gap: '1rem',
            marginTop: '2rem'
          }}>
            {pathologies.map((pathology, index) => (
              <div key={index} style={{
                background: '#f8fafc',
                padding: '1rem',
                borderRadius: '8px',
                textAlign: 'center',
                border: '2px solid #e2e8f0',
                transition: 'all 0.3s ease'
              }}>
                <span style={{ color: '#2563eb', fontWeight: '500' }}>{pathology}</span>
              </div>
            ))}
          </div>
        </div>
      </div>
    </section>
  );
};

export default Services;
